import { useState ,useEffect } from "react"
import React from 'react'

const Aktuelles = () => {

    const [posts, setPosts] = useState([]);


useEffect(() => {
    const intervalId = setInterval(() => {
        fetch(`https://graph.facebook.com/v16.0/me?fields=id%2Cname%2Cphotos%7Bimages%2Cpicture%7D%2Cposts%7Bfull_picture%2Cname%7D&access_token=EAAMos5QZAUGsBAJU9W5wTV2HeXvh9LZAKCnbdBDpzjUUmk7ZBp12yFVPwYjCUGR0ZC2cAGNmqfka6DBXWE0qZChjVBgwd9pkHikeBUmHF3imo4026EZBiAlRpQZBZCXSQPNP8kIe5ChJcZBqnhnqfKKZAZCSbZCd62QZCYzX6EZBQHZCpf2dViKPDJLQRHp29XGJU80i9gEuffjTwvw4uhUQmjHv0gM`)
          .then(response => response.json())
          .then(data => setPosts(data.data))
          .catch(error => console.error(error));
         
      }, 6000); // Fetch new posts every 1 minute
      console.log(posts)
      return () => clearInterval(intervalId);
}, [posts]);

if(posts.length > 0){

    const mappedPost =posts.map(post => (
        <div key={post.id}>
          <p>{post.message}</p>
          <img src={post.full_picture} alt={post.message} />
        </div>
      ))
    
}
    


  return (
    <div>{mappedPost}</div>
  )
}

export default Aktuelles
