import React, { useState } from 'react';
import classes from "./contactEmail.module.css"

const ContactEmail = () => {
  const [email, setEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');
  const [messageSent, setMessageSent] = useState(false);

  const handleSubmit = (event) => {
    // event.preventDefault();
    // Here you can add the code to send the email
    console.log(`Email: ${email}\nSubject: ${subject}\nMessage: ${message}`);
    setMessageSent(true);
  };

  return (
    <form className={classes["contact-form"]} onSubmit={handleSubmit} action={`mailto:${email}?subject=${subject}body=${message}`}>
      <h3>Kontakt per E-Mail</h3>
      {messageSent && <h3>Deine Nachricht wird an deine Email Dienst weitergeleitet</h3>}
      <div className={classes["form-group"]}>
        <label className={classes["form-label"]} htmlFor="email">Email:</label>
        <input
          className={classes["form-input"]}
          type="email"
          id="email"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
          required
        />
      </div>
      <div className={classes["form-group"]}>
        <label className={classes["form-label"]} htmlFor="subject">Subject:</label>
        <input
          className={classes["form-input"]}
          type="text"
          id="subject"
          value={subject}
          onChange={(event) => setSubject(event.target.value)}
          required
        />
      </div>
      <div className={classes["form-group"]}>
        <label className={classes["form-label"]} htmlFor="message">Message:</label>
        <textarea
          className={classes["form-input"]}
          id="message"
          value={message}
          onChange={(event) => setMessage(event.target.value)}
          required
        />
      </div>
      <button className={classes["form-button"]} type="submit">Send</button>
    </form>
  );
};

export default ContactEmail;
