import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

import ImgSlider from "../carousel/ImgSlider";

import Intro from "../intro/Intro";
import ZuUns from "../zuUns/ZuUns";
import classes from "./home.module.css";
import Wieso from "../wieso/Wieso";
import Collage from "../collage/Collage";

import Projekte from "../projekte/Projekte";
import Geschichte from "../geschichte/Geschichte";



export const Home = ({ title}) => {

  const location = useLocation();

  useEffect(() => {
    document.title = title; // Set the title when the component mounts
  }, [title]);

  useEffect(() => {
    document.title = title; // Update the title when the location changes
  }, [location, title]);



  return (
    <div className={classes["home-container"]}>
    
      <ImgSlider />

      <Intro />
      <Wieso></Wieso>
      <ZuUns />
      <Collage/>
      <Projekte/>
      <Geschichte/>

    </div>
  );
};
