import { useState ,useEffect } from "react"
import React from 'react'

const Aktuelles = () => {

    const [posts, setPosts] = useState([]);
  
   
useEffect(() => {
    const intervalId = setInterval(() => {
        fetch(` "https://graph.facebook.com/v16.0/me?fields=id%2Cname%2Cphotos%7Bimages%2Cpicture%7D%2Cposts%7Bfull_picture%2Cname%7D&access_token=EAAMos5QZAUGsBAPZB3JI2uelqw9jWZCDZAtZCZCQWVEwRK8yzGf4NqhiYA47U3IhHX5bY5fOYM7ZBkxaOmI2WZAauU9p98LJPZC99I1WGtZC69mNdGQoKcGGnbV9cqnBlFj2WtIoTpZAZCLIOZAWEfjItJDHpEt2ZCBRw8DMjClFfIBen06RImBnxti4404w4FZCq0HqECIqSQsqUVZCbQGon7Cr3RqNWW1Gkq7xNhLbpZBhAhmy2hWw0eU0WK8dl"`)
          .then(response => response.json())
          .then(data => setPosts(data.data))
          .catch(error => console.error(error));
      }, 60000); // Fetch new posts every 1 minute
      console.log(posts)
      return () => clearInterval(intervalId);
}, [posts]);

const mappedPost =posts.map(post => (
    <div key={post.id}>
      <p>{post.message}</p>
      <img src={post.full_picture} alt={post.message} />
    </div>
  ))

  return (
    <div>{mappedPost}</div>
  )
}

export default Aktuelles
