import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import styles from './EmailForm.module.css';

const EmailForm = () => {
  const [toEmail, setToEmail] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const templateParams = {
      to_email: toEmail,
      subject: subject,
      body: body
    };

    emailjs.send('<YOUR_SERVICE_ID>', '<YOUR_TEMPLATE_ID>', templateParams, '<YOUR_USER_ID>')
      .then((response) => {
        console.log('SUCCESS!', response.status, response.text);
      }, (error) => {
        console.log('FAILED...', error);
      });

    setToEmail('');
    setSubject('');
    setBody('');
  };

  return (
    <form className={styles.formContainer} onSubmit={handleSubmit}>
      <label className={styles.formLabel}>
        To:
        <input className={styles.formInput} type="email" value={toEmail} onChange={(e) => setToEmail(e.target.value)} required />
      </label>
      <label className={styles.formLabel}>
        Subject:
        <input className={styles.formInput} type="text" value={subject} onChange={(e) => setSubject(e.target.value)} required />
      </label>
      <label className={styles.formLabel}>
        Body:
        <textarea className={styles.formTextarea} value={body} onChange={(e) => setBody(e.target.value)} required />
      </label>
      <button className={styles.formButton} type="submit">Send Email</button>
    </form>
  );
};

export default EmailForm;
