import React, { useState, useEffect } from 'react';

const Aktuelles = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('https://graph.facebook.com/100069270243759/posts?fields=full_picture,message,created_time');
      const data = await response.json();
      setPosts(data.data);
    } catch (error) {
      console.error('Error fetching Facebook posts:', error);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const renderPosts = () => {
    return posts.map((post) => (
      <div key={post.id} className="fb-post" data-href={`https://www.facebook.com/${post.id}`}>
        <h3>{formatDate(post.created_time)}</h3>
        <img src={post.full_picture} alt="Post" />
        <p>{post.message}</p>
      </div>
    ));
  };

  return (
    <div>
      <h2>Aktuelles</h2>
      <div>{renderPosts()}</div>
    </div>
  );
};

export default Aktuelles;
