import { useState ,useEffect } from "react"
import React from 'react'

const Aktuelles = () => {

    const [posts, setPosts] = useState([]);
  let mappedPost=[]

useEffect(() => {
    const intervalId = setInterval(() => {
        fetch(`curl -i -X GET \
        "https://graph.facebook.com/v16.0/me?fields=id%2Cname%2Cphotos%7Bimages%2Cpicture%2Cname%7D%2Cposts%7Bfull_picture%2Cname%2Cdescription%7D&access_token=EAAMos5QZAUGsBACkScCEiwIvbZCjcZBpaQcUDOPc8TsTrVYmrO2vl8PjxqOZA8GRrZCUZCr8XCFcujI6ywx5zh7nPZC8uviaNrZCYDL1w7xO1Tt5rzpe35JhAwMbiOguiulnbw69CvuZBFxkZBs4UpZC2XWlaDeRDeMChqn9NSqTfoRZC6ruudXVhGVy5LPGAiK2auHGYtC494enZBHsbzUgZA3bCMUWqus8EjLZCRv5ef44q7ZC22lLSTZCSSrpt"`)
          .then(response => response.json())
          .then(data => setPosts(data.data))
          .catch(error => console.error(error));
         
      }, 600000); // Fetch new posts every 1 minute
      console.log(posts)
      return () => clearInterval(intervalId);
}, [posts]);

if(posts){

     mappedPost =posts.map(post => (
        <div key={post.id}>
          <p>{post.message}</p>
          <img src={post.full_picture} alt={post.message} />
        </div>
      ))
    
}else{
    mappedPost = <h1>Loading...</h1>
}
    


  return (
    <div>{mappedPost}</div>
  )
}

export default Aktuelles
